const buyLinks=document.querySelectorAll(".good-options__button--buy"),buyPopup=document.querySelector(".modal-buy"),buyClose=buyPopup.querySelector(".modal__close");buyLinks.forEach(o=>{o.addEventListener("click",function(o){o.preventDefault(),buyPopup.classList.add("modal__show")})}),buyClose.addEventListener("click",function(o){o.preventDefault(),buyPopup.classList.remove("modal__show")}),window.addEventListener("keydown",function(o){27===o.keyCode&&buyPopup.classList.contains("modal__show")&&(o.preventDefault(),buyPopup.classList.remove("modal__show"))});